package com.string;

public class PrintEachCharecterInString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="WelcomeToNalgonda";
		
		
		 int count =0;
		 
		 for(int i=0;i<s1.length();i++) {
			 count++;
			 
			 if(i<s1.length()-1);
			 
			 
			
				 System.out.println(s1.charAt(i));
			 System.out.println("Total Charecters In The Streing Are ;"+count);
		 }

	}

}
