package com.string;

public class SeperateTheInduividualCharacterFromA_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str= "KOMATIBHARATHWAJ";
		
		System.out.println("Induvidual characters in the given string:");
		
		for (int i=0;i<str.length();i++) {		
		
		System.out.print(str.charAt(i)+ "  ");

	}

}
}